<template lang="">
  <section class="product-section">
    <div class="container-product">
      <div class="content-header">
        <h2>제품 목록</h2>
        <AdminButton />
      </div>
      <div class="card mb-4">
        <div class="card-body">
          <table>
            <div
              v-for="(product, index) in products"
              :key="product.id"
              class="row">
              <div
                v-for="column in columns"
                :key="column.name"
                :class="column.col">
                <template v-if="column.field === 'index'">
                  {{ index + 1 }}
                </template>

                <template v-else-if="column.field === 'thumbnail'">
                  <img
                    class="thumbnail"
                    :src="
                      product.thumbnail
                        ? product.thumbnail
                        : 'data:image/jpeg;base64,'
                    "
                    alt="">
                </template>

                <template v-else-if="column.field === 'isSoldOut'">
                  <span
                    v-if="product[column.field]"
                    class="badge rounded-pill alert-danger">매진</span>
                  <span
                    v-else
                    class="badge rounded-pill alert-success">주문가능</span>
                </template>
                <template v-else-if="column.field === 'dropdown'" />
                <template v-else-if="column.field === 'tags'">
                  {{ product.tags.toString() }}
                </template>
                <template v-else-if="column.field === 'price'">
                  {{ product.price.toLocaleString() + '원' }}
                </template>
                <template v-else>
                  {{ product[column.field] }}
                </template>
              </div>
              <div class="dropdown float-end">
                <button
                  id="dropdownMenuButton1"
                  class="dropdown-toggle btn"
                  data-toggle="dropdown"
                  aria-haspopup="true"
                  aria-expanede="false">
                  <span class="material-symbols-outlined">more_horiz</span>
                </button>
                <div class="dropdown-menu">
                  <button
                    href="#"
                    class="dropdown-item">
                    <RouterLink
                      :to="{
                        name: 'EditProduct',
                        params: {
                          id: product.id,
                          oldTitle: product.title,
                          oldPrice: product.price,
                          oldDescription: product.description,
                          oldTags: product.tags.toString(),
                          oldThumbnail: product.thumbnail,
                          oldIsSoldOut: product.isSoldOut,
                        },
                      }">
                      Edit Product
                    </RouterLink>
                  </button>
                  <button
                    class="dropdown-item"
                    @click="deleteProduct(product)">
                    Delete Product
                  </button>
                </div>
              </div>
            </div>
          </table>
        </div>
      </div>
    </div>
  </section>
</template>
<script>
import AdminButton from '../../components/AdminButton.vue'
import axios from 'axios'
import noImage from '/src/assets/noImage'
const { VITE_API_KEY, VITE_USERNAME } = import.meta.env

export default {
  components: {
    AdminButton,
  },
  data() {
    return {
      columns: [
        { field: 'index', name: '', col: 'column flex-grow-0 col-index' },
        {
          field: 'thumbnail',
          name: '이미지',
          col: 'column col-lg-auto col-sm-auto col-auto col-thumbnail',
        },
        {
          field: 'title',
          name: '제목',
          col: 'column col-lg-3 col-sm-2 col-5 col-title',
        },
        {
          field: 'price',
          name: '가격',
          col: 'column col-lg-2 col-sm-2 col-3 col-price',
        },
        {
          field: 'tags',
          name: '태그',
          col: 'column col-lg-2 col-sm-2 col-4 col-tags',
        },
        {
          field: 'isSoldOut',
          name: '매진여부',
          col: 'column col-lg-2 col-sm-2 col-4 col-isSoldOut',
        },
      ],
    }
  },
  computed: {
    products() {
      return this.$store.state.admin.productList
    },
  },
  created() {
    this.$store.dispatch('admin/readProducts')
  },
  methods: {
    async deleteProduct(product) {
      const { id, thumbnail } = product
      try {
        const res = await axios({
          url: `https://asia-northeast3-heropy-api.cloudfunctions.net/api/products/${id}`,
          headers: {
            'content-type': 'application/json',
            apikey: VITE_API_KEY,
            username: VITE_USERNAME,
            masterKey: true,
          },
          method: 'DELETE',
        })
        console.log(res)
        this.$store.dispatch('admin/readProducts')
        this.$swal({
          title: '제품이 삭제되었습니다!',
          icon: 'success',
          imageUrl: thumbnail,
          imageWidth: 100,
          imageHeight: 100,
          imageAlt: noImage,
          width: 400,
          confirmButtonColor: '#f2555a',
        })
      } catch (error) {
        console.log(error)
      }
    },
  },
}
</script>
